machine_start_gcode.default_value:
; ==========================
; Machine-start gcode
;
M76 ; Pause print job timer until the print job starts
;
M107   ; Turn layer fan off
G21    ; Set to metric 
G90    ; Force coordinates to be absolute (relative to the origin)
;
M104 R{material_print_temperature_layer_0}      ; Initiate hotend preheat
M140 R{material_bed_temperature_layer_0 + 10}   ; Initiate bed preheat 
;
; Auto Home
M117 Auto Homing ...
G28
;
M109 R{material_print_temperature_layer_0}  ; Preheat hotend temp
M190 R{material_bed_temperature_layer_0}    ; Preheat bed temperature
;
M117 Cleaning ...
G92 E0                  ; Set extruder position
G1 E1 F500              ; Prime extruder
G0 X-10 Y10 Z0.3 F9000  ; Prepare for extruder wipe
G0 X10 Y10 Z0.3 F9000   ; Wipe against bed edge
G1 E-1 F1800            ; Retract to avoid oozing
G92 E0                  ; Reset extruder to [0] zero end of cleaning run
;
M75 ; Start print job timer
;
M117 Est. time: {print_time}
;
; ==========================
;

machine_end_gcode.default_value:
;
; ==========================
; Machine-end gcode
;
M107                            ; Turn off layer fan
;
G92 E0                          ; Set extruder position 
G1 E-{retraction_amount} F1800  ; Retract filament
G0 X0 Y{machine_depth} F6000    ; Position bed for part removal
;
M104 S0                         ; Turn off hotend
M140 S0                         ; Turn off bed 
;
M84                             ; Disable motors
;
; ==========================
;
